import Footer from "../../components/Footer/Footer";
import Header from "../../components/Header/Header";
import NavBar from "../../components/NavBar/NavBar";
import { useState, useEffect } from "react";
import Card from "../../components/Card/Card";

export default function Ampli() {
  const [dataNav, setDataNav] = useState([]);
  useEffect(() => {
    fetch("../datas.json")
      .then((res) => res.json())
      .then((res2) => setDataNav(res2))
      .catch((error) => console.log(error));
  }, []);

  const ampliFilter = dataNav.filter((el) => el.ampli || el.effet === true);

  return (
    <>
      <Header />
      <NavBar />
      <div className="cards cards--filter-active">
        {ampliFilter.map(({ title, description, price, like, picture, id }) => {
          return (
            <Card
              title={title}
              description={description}
              price={price}
              like={like}
              picture={picture}
              id={id}
            />
          );
        })}
      </div>
      <Footer />
    </>
  );
}
