import { useEffect, useState } from "react";

export default function Button({ title, description, price }) {
  let basket = localStorage.getItem("basket") || "[]";

  const [addProd, setAddProd] = useState({
    title: title,
    description: description,
    price: price,
  });

  function addToCart() {
    useEffect(() => {
      localStorage.setItem("basket", JSON.stringify(addProd));
    }, []);
  }

  return (
    <button className="button_addtocart" onClick={addToCart()}>
      Ajouter au panier
    </button>
  );
}
