import { useState } from "react";
import { useParams } from "react-router-dom";
import { useLocation } from "react-router-dom";

export default function Button({ title, description, price, picture }) {
  let location = useLocation();
  const display = location.pathname === "/basket" ? "" : "display-none";
  console.log(display);
  const { id } = useParams();
  const [addProd, setAddProd] = useState({
    title: title,
    description: description,
    price: price,
    picture: picture,
    id: parseInt(id),
  });

  function addToCart() {
    let basket = JSON.parse(localStorage.getItem("basket") || "[]");
    const foundProduct = basket.find((e) => e.title === title);
    if (foundProduct != undefined) {
      localStorage.setItem("basket", JSON.stringify(basket));
      alert("Votre article à bien été ajouté au panier");
    } else {
      basket.push(addProd);
      localStorage.setItem("basket", JSON.stringify(basket));
      alert("Votre article à bien été ajouté au panier");
    }
  }
  // function deleteItem() {
  //   let basket = JSON.parse(localStorage.getItem("basket") || "[]");
  //   const foundProduct = basket.filter((e) => e.title === title);
  //   if (foundProduct != undefined) {
  //     localStorage.setItem("basket", JSON.stringify(basket));
  //     alert("Votre article à bien été ajouté au panier");
  //   }
  // }
  return (
    <>
      <button className="button_addtocart" onClick={addToCart}>
        Ajouter au panier
      </button>
      <button className={"delete_product " + display}>Supprimer</button>
    </>
  );
}
