import { useEffect, useState } from "react";

export default function Button({ title, description, price }) {
  let basket = localStorage.getItem("basket") || "[]";

  const [addProd, setAddProd] = useState({
    name: title,
    description: description,
    prix: price,
  });

  useEffect(() => {
    localStorage.setItem("basket", JSON.stringify(addProd));
  }, []);

  return (
    <button className="button_addtocart" onClick={addProd}>
      Ajouter au panier
    </button>
  );
}
