// import { useNavigate } from "react-router-dom";
import { useState } from "react";

export default function Form() {
  //   const navigate = useNavigate();
  //   const navOrder = navigate("/order");
  const [inputState, setInputState] = useState("");
  const [lastNameState, setLastNameState] = useState("");

  const handleInput = (e) => {
    setInputState(e.target.value);
  };
  const log = console.log(inputState);
  return (
    <div className="cart__order">
      <form method="get" className="cart__order__form">
        <div className="cart__order__form__question">
          <label htmlFor="firstName">Pr√©nom: </label>
          <input
            type="text"
            name="firstName"
            id="firstName"
            onChange={handleInput}
            required
          ></input>
        </div>
        <p id="firstNameErrorMsg"></p>
        <div className="cart__order__form__question">
          <label htmlFor="lastName">Nom: </label>
          <input
            type="text"
            name="lastName"
            onChange={handleInput}
            required
          ></input>
        </div>
        <p id="lastNameErrorMsg"></p>
        <div className="cart__order__form__question">
          <label htmlFor="adress">Adresse: </label>
          <input
            type="text"
            name="adress"
            id="adress"
            onChange={handleInput}
            required
          ></input>
        </div>
        <p id="adressErrorMsg"></p>
        <div className="cart__order__form__question">
          <label htmlFor="city">Ville: </label>
          <input
            type="text"
            name="city"
            id="city"
            onChange={handleInput}
            required
          ></input>
        </div>
        <p id="cityErrorMsg"></p>
        <div className="cart__order__form__question">
          <label htmlFor="email">Email: </label>
          <input
            type="text"
            name="email"
            id="email"
            onChange={handleInput}
            required
          ></input>
        </div>
        <p id="emailErrorMsg"></p>
        <div className="cart__order__submit">
          <input
            type="submit"
            id="order"
            value="Commander !"
            className="button_addtocart"
            onClick={log}
          ></input>
        </div>
      </form>
    </div>
  );
}
