import Header from "../../components/Header/Header";
import Card from "../../components/Card/Card";
import Footer from "../../components/Footer/Footer";
import Button from "../../components/ButtonAddToCart/button";

export default function Cart() {
  const basket = JSON.parse(localStorage.getItem("basket") || "[]");
  const totalProduct = basket.length;
  if (totalProduct === 0) {
    return null;
  }
  if (totalProduct === 1) {
    return "1 article";
  }
  console.log(totalProduct);

  return (
    <>
      <Header />
      <section className="section_cart">
        <div className="container_basket">
          <h3 className="title_basket "> Votre panier</h3>
          {basket.map(({ title, description, price, picture, id }) => (
            <Card
              title={title}
              description={description}
              price={price}
              key={price + title + description}
              picture={picture}
              id={id}
            />
          ))}
          <h3 className="total">Total( ): â‚¬</h3>
        </div>
      </section>
      <Footer />
    </>
  );
}
