import Header from "../../components/Header/Header";
import Card from "../../components/Card/Card";
import useFetch from "../../utils/hooks/index";
import { useState } from "react";

export default function Cart() {
  let basket = JSON.parse(localStorage.getItem("basket") || "[]");
  const [pictureFetch, setPictureFetch] = useState();

  const { data } = useFetch("../datas.json");
  for (let a = 0; a < basket.length; a++) {
    const product = basket[a];
    const foundProduct = data.find((el) => el.title === product.title);
    setPictureFetch(foundProduct.picture);
  }
  console.log(pictureFetch);
  return (
    <>
      <Header />
      <section className="section_cart">
        <div className="container_basket">
          {basket.map(({ title, description, price }) => (
            <Card
              title={title}
              description={description}
              price={price}
              key={price + title + description}
            />
          ))}
        </div>
      </section>
    </>
  );
}
