import { useEffect, useState } from "react";

export default function Button({ title, description, price }) {
  const [addProd, setAddProd] = useState({
    title: title,
    description: description,
    price: price,
  });

  useEffect((panier) => {
    let basket = localStorage.getItem("basket") || "[]";
    localStorage.setItem("basket", JSON.stringify(panier));
  }, []);

  return (
    <button className="button_addtocart" onClick={panier.push(addProd)}>
      Ajouter au panier
    </button>
  );
}
