import { useState } from "react";

export default function Button({ title, description, price }) {
  const basket = { title, description, price };
  function addToCart(basket) {
    localStorage.setItem("basket", JSON.stringify(basket));
  }
  return (
    <button className="button_addtocart" onClick={addToCart()}>
      Ajouter au panier
    </button>
  );
}
