import { useState } from "react";

export default function Button({ title, description, price }) {
  let basket = localStorage.getItem("basket") || "[]";

  const [addProd, setAddProd] = useState();
  setAddProd({ title, price, description });

  function addToCart(addProduct) {
    localStorage.setItem("basket", JSON.stringify(addProduct));
  }
  return (
    <button className="button_addtocart" onClick={addToCart(addProduct)}>
      Ajouter au panier
    </button>
  );
}
