export default function Button({ title, description, price }) {
  let basket = JSON.parse(localStorage.getItem("basket") || "[]");

  function addToCart() {
    localStorage.setItem("basket", JSON.stringify());
  }
  return (
    <button className="button_addtocart" onClick={addToCart()}>
      Ajouter au panier
    </button>
  );
}
