// import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";

export default function Form() {
  //   const navigate = useNavigate();
  //   const navOrder = navigate("/order");
  const [firstNameState, setFirstNameState] = useState("");
  const [lastNameState, setLastNameState] = useState("");
  const [adressState, setAdressState] = useState("");
  const [cityState, setCityState] = useState("");
  const [emailState, setEmailState] = useState("");

  const handleInput1 = (e) => {
    setFirstNameState(e.target.value);
  };
  const handleInput2 = (e) => {
    setLastNameState(e.target.value);
  };
  const handleInput3 = (e) => {
    setAdressState(e.target.value);
  };
  const handleInput4 = (e) => {
    setCityState(e.target.value);
  };
  const handleInput5 = (e) => {
    setEmailState(e.target.value);
  };

  return (
    <div className="cart__order">
      <form method="get" className="cart__order__form">
        <div className="cart__order__form__question">
          <label htmlFor="firstName">Pr√©nom: </label>
          <input
            type="text"
            name="firstName"
            id="firstName"
            onChange={handleInput1}
            required
          ></input>
        </div>
        <p id="firstNameErrorMsg"></p>
        <div className="cart__order__form__question">
          <label htmlFor="lastName">Nom: </label>
          <input
            type="text"
            name="lastName"
            onChange={handleInput2}
            required
          ></input>
        </div>
        <p id="lastNameErrorMsg"></p>
        <div className="cart__order__form__question">
          <label htmlFor="adress">Adresse: </label>
          <input
            type="text"
            name="adress"
            id="adress"
            onChange={handleInput3}
            required
          ></input>
        </div>
        <p id="adressErrorMsg"></p>
        <div className="cart__order__form__question">
          <label htmlFor="city">Ville: </label>
          <input
            type="text"
            name="city"
            id="city"
            onChange={handleInput4}
            required
          ></input>
        </div>
        <p id="cityErrorMsg"></p>
        <div className="cart__order__form__question">
          <label htmlFor="email">Email: </label>
          <input
            type="text"
            name="email"
            id="email"
            onChange={handleInput5}
            required
          ></input>
        </div>
        <p id="emailErrorMsg"></p>
        <div className="cart__order__submit">
          <input
            type="submit"
            id="order"
            value="Commander !"
            className="button_addtocart"
            onClick={log}
          ></input>
        </div>
      </form>
    </div>
  );
}
